INSERT INTO filmek(kod, nev, leiras, kep1, kep2, kep3, kep4, kep5, kep6, fokep) VALUES("bw_PgBizOoQ", "Wonka", "Willy Wonka (Timothée Chalamet) igazi legenda. De még a legendák is voltak fiatalok. Csak egészen másképpen, mint bármelyik másik ember. Végre lehull a lepel a csokik mágusa, az édességek géniusza, a legkülönlegesebb, legízesebb, legfurább csokigyáros, Willy Wonka életének nagy titkáról.", "https://media.port.hu/images/001/609/440.webp", "https://media.port.hu/images/001/602/436.webp", "https://media.port.hu/images/001/609/438.webp", "https://media.port.hu/images/001/609/436.webp", "https://media.port.hu/images/001/609/434.webp", "https://media.port.hu/images/001/609/432.webp", "https://media-cldnry.s-nbcnews.com/image/upload/t_social_share_1200x630_center,f_auto,q_auto:best/rockcms/2023-12/willy-wonka-mc-231211-03-2b6c32.jpg");
