INSERT INTO filmek(kod, nev, leiras, kep1, kep2, kep3, kep4, kep5, kep6, fokep) VALUES("PeWnAFeqMUA", "Elemi", "A Disney és a Pixar új filmje, az ELEMI egy olyan városban játszódik, ahol egymás mellett élnek a tűz-, víz-, föld- és levegőlakók. Parázs, a talpraesett, gyors észjárású és tüzes fiatal nő, illetve Örvény, a mókás, életvidám, laza srác találkozása alapjaiban változatatja meg mindazt, amit az őket körülvevő világról hittek.", "https://media.port.hu/images/001/575/094.webp", "https://media.port.hu/images/001/575/096.webp", "https://media.port.hu/images/001/575/098.webp", "https://s3.us-east-1.amazonaws.com/world-website-storage/wng-prod/_1500x937_crop_center-center_82_line/1774087/elemental13.jpg", "https://www.columbian.com/wp-content/uploads/2023/06/Film_Review_-_Elemental_55099-315ec.jpg", "https://media.npr.org/assets/img/2023/06/12/elemental-online-use-m551_11_pub.pub16.158_custom-7ec940496b02b4a347356a2de3f45dd612805184.jpg", "https://sm.ign.com/ign_hu/screenshot/default/elemental-ver10-xxlg-crop_56g8.jpg");
INSERT INTO filmek(kod, nev, leiras, kep1, kep2, kep3, kep4, kep5, kep6, fokep) VALUES("1mR3r0JkJDI", "Oppenheimer", "A film J. Robert Oppenheimer fizikusról szól, aki kollégáival - köztük a magyar származású Teller Edével és Szilárd Leóval - a Manhattan Terv vezetőjeként az első atomfegyver létrehozásáért felelős. A találmány a II. világháborúban megváltoztatta a történelem menetét.", "https://media.port.hu/images/001/581/442.webp", "https://media.port.hu/images/001/581/440.webp", "https://media.port.hu/images/001/581/444.webp", "https://media.port.hu/images/001/580/288.webp", "https://media.port.hu/images/001/580/290.webp", "https://media.port.hu/images/001/553/656.webp", "https://ma7media.storage.googleapis.com/sites/default/files/styles/freeform_large_9_2x/s3/2023-08/oppenheimer-cimlap.png.jpg?h=920929c4&itok=N-I4qz3T");
