INSERT INTO filmek(kod, nev, leiras, kep1, kep2, kep3, kep4, kep5, kep6, fokep) VALUES("-tGEsyuMnRA", "Dűne: Második rész", "A távoli jövőben, a bolygóközi királyságok korában játszódó történetben két nagyhatalmú uralkodóház harcol az Arrakis bolygó feletti hatalomért, mert az ismert univerzumban egyedül az itteni végtelen sivatagban bányászható az a fűszer, amely lehetővé teszi a csillagközi utazást.", "https://media.port.hu/images/001/620/938.webp", "https://media.port.hu/images/001/620/918.webp", "https://media.port.hu/images/001/620/934.webp", "https://media.port.hu/images/001/620/930.webp", "https://media.port.hu/images/001/620/920.webp", "https://media.port.hu/images/001/620/922.webp", "https://s.24.hu/app/uploads/2024/03/dune2-e1709485726276-1024x576.jpg");
INSERT INTO filmek(kod, nev, leiras, kep1, kep2, kep3, kep4, kep5, kep6, fokep) VALUES("vbDgdb58l2k", "Kung Fu Panda 4", "Csaknem egy évtized után tavasszal visszatér a legkülönösebb kungfu mester a DreamWorks Animation fergeteges sorozatának baresz új fejezetében. Bátor Sárkányharcosként végigcsinált három halált megvető kalandot, és most a sors újabb feladat elé állítja: pihenjen már egy kicsit. Pontosabban hogy legyen a Békevölgy szellemi vezetője.", "https://media.port.hu/images/001/625/894.webp", "https://media.port.hu/images/001/625/892.webp", "https://media.port.hu/images/001/625/890.webp", "https://media.port.hu/images/001/625/886.webp", "https://media.port.hu/images/001/625/884.webp", "https://media.port.hu/images/001/609/646.webp", "https://i0.wp.com/www.thewrap.com/wp-content/uploads/2023/12/kung-fu-panda-4-image.jpg");
INSERT INTO filmek(kod, nev, leiras, kep1, kep2, kep3, kep4, kep5, kep6, fokep) VALUES("sETzLAJ7n74", "A kaszkadőr", "Hősünk kaszkadőr, és mint mindenkit a kaszkadőrközösségben, őt is felrobbantják, lelövik, elütik, kidobják ablakokon és leejtik magas helyekről, a mi szórakoztatásunkra.", "https://media.port.hu/images/001/601/072.webp", "https://media.port.hu/images/001/601/074.webp", "https://media.port.hu/images/001/601/076.webp", "https://media.port.hu/images/001/601/078.webp", "https://media.port.hu/images/001/601/072.webp", "https://media.port.hu/images/001/601/076.webp", "https://image.tmdb.org/t/p/original/eFNXMvbgCkfBy35oOjRjgdnkVuk.jpg");
INSERT INTO filmek(kod, nev, leiras, kep1, kep2, kep3, kep4, kep5, kep6, fokep) VALUES("FXMF_WAEiH4", "Az első ómen", "Egy fiatal amerikai nőt Rómába küldenek, hogy az egyház szolgálatának szentelje életét. Ott azonban olyan sötétséggel találkozik, ami kétségeket ébreszt tulajdon hitében, s egy borzongató összeesküvésre is fényt derít, aminek célja, hogy biztosítsa a megtestesült gonosz világra jövetelét.", "https://media.port.hu/images/001/631/914.webp", "https://media.port.hu/images/001/631/902.webp", "https://media.port.hu/images/001/631/912.webp", "https://media.port.hu/images/001/631/900.webp", "https://media.port.hu/images/001/631/898.webp", "https://media.port.hu/images/001/613/914.webp", "https://www.hollywoodreporter.com/wp-content/uploads/2024/03/TFO-07276-H-2024.jpg");
